    const data = loader3.parse( text );

    const paths = data.paths;

    const shapes = [];

    for ( let i = 0; i < paths.length; i++ ) {
      Array.prototype.push.apply( shapes, paths[ i ].toShapes() ); // catenate, so we can create a single geometry and mesh
